CREATE ALGORITHM=UNDEFINED DEFINER=`homestead`@`%` SQL SECURITY DEFINER VIEW `equipments_v` AS select `e`.`id` AS `id`,`e`.`uuid` AS `uuid`,`e`.`equipment_name` AS `equipment_name`,`e`.`equipment_status` AS `equipment_status`,count(`f`.`id`) AS `facilities` from ((`equipment` `e` join `facility_equipment_mapping` `fem` on((`fem`.`equipment_id` = `e`.`id`))) join `facility` `f` on((`f`.`facility_code` = `fem`.`facility_code`))) group by `e`.`id`